import React, { Component } from "react";
import ProjectImgLeft from "./ProjectImgLeft/ProjectImgLeft";
import ProjectImgRight from "./ProjectImgRight/ProjectImgRight";
import ScrollAnimation from "react-animate-on-scroll";
import "./Showcases.scss";
import axios from "axios";

export class Showcases extends Component {
  constructor(props) {
    super(props);

    this.state = {
      projects: [],
    };
  }

  showHideDesc = (i) => {
    let projects = [...this.state.projects];
    projects[i].closed = !this.state.projects[i].closed;
    this.setState({ projects });
  };

  componentDidMount() {
    axios
      .get("https://change-5b13b.firebaseio.com/projects.json")
      .then((response) => {
        this.setState({
          projects: Object.values(response.data),
        });
      });

    /*const auth = firebase.auth();
    auth.signInWithEmailAndPassword("moldovanoid@gmail.com", "yoArb$1").then(
      firebase.auth().onAuthStateChanged((firebaseUser) => {
        if (firebaseUser) {
          firebase.database().ref('/projects/').remove();
          firebase
            .database()
            .ref("/projects")
            .push(project3)
            .catch(function err() {
              console.log(err);
            });
        }
      })
    );*/
  }

  render() {
    let title = (
      <div key={0} className="showcasesRow row">
        <div className="col-10 col-sm-10 col-md-10 col-lg-10 col-xl-10 offset-1 offset-sm-1 offset-md-1 offset-lg-1 offset-xl-1">
          <span>
            <i id="wedo" className="showcasesArrow icono-arrow1-right"></i>
          </span>
          <div className="font1 showCasesTitle">Showcases</div>

          <div className="font2 showCasesSubtitle">Not only blablablabla.</div>
        </div>
      </div>
    );
    let moreComing = (
      <div key={99} className="row">
        <div className="col-10 col-sm-10 col-md-10 col-lg-10 col-xl-10 offset-1 offset-sm-1 offset-md-1 offset-lg-1 offset-xl-1">
          <span>
            <i className="moreComingArrow icono-arrow1-right"></i>
          </span>
          <div className="font6 moreComing">More coming soon.</div>
        </div>
      </div>
    );

    if (this.state.projects) {
      return [
        title,
        this.state.projects.map((project, i) => {
          if (i % 2 === 0)
            return (
              <ScrollAnimation delay={150} key={i} animateOnce={true} animateIn="slideInRight">
                <ProjectImgLeft
                  id={i}
                  imgLink={`${project.imgLink}`}
                  title={`${project.title}`}
                  titleSlim={`${project.titleSlim}`}
                  content={project.closed ? "Show more." : "Show less."}
                  subtitle={`${project.subtitle}`}
                  showHide={this.showHideDesc.bind(this, i)}
                  description={`${project.description}`}
                />
              </ScrollAnimation>
            );
          else
            return (
              <ScrollAnimation delay={150} key={i} animateOnce={true} animateIn="slideInLeft">
                <ProjectImgRight
                  id={i}
                  imgLink={`${project.imgLink}`}
                  title={`${project.title}`}
                  titleSlim={`${project.titleSlim}`}
                  content={project.closed ? "Show more." : "Show less."}
                  subtitle={`${project.subtitle}`}
                  showHide={this.showHideDesc.bind(this, i)}
                  description={`${project.description}`}
                />
              </ScrollAnimation>
            );
        }),
      moreComing];
    }
  }
}

export default Showcases;
