import React, { Component } from "react";
import Showcase from "./Showcase/Showcase";
import ScrollAnimation from "react-animate-on-scroll";
import "./Showcases.scss";
import axios from "axios";

export class Showcases extends Component {
  constructor(props) {
    super(props);

    this.state = {
      projects: [],
    };
  }

  showHideDesc = (i) => {
    let projects = [...this.state.projects];
    projects[i].closed = !this.state.projects[i].closed;
    this.setState({ projects });
  };

  componentDidMount() {
    axios
      .get("http://localhost:5000/showcases/")
      .then((response) => {
        console.log(response)
        this.setState({
          projects: Object.values(response.data),
        });
      })
  }

  render() {
    
        let title = (
      <div key={0} className="showcasesRow row">
        <div className="col-10 col-sm-10 col-md-10 col-lg-10 col-xl-10 offset-1 offset-sm-1 offset-md-1 offset-lg-1 offset-xl-1">
          <span>
            <i id="wedo" className="showcasesArrow icono-arrow1-right"></i>
          </span>
          <div className="font1 showCasesTitle">Showcases</div>

          <div className="font2 showCasesSubtitle">Not only blablablabla.</div>
        </div>
      </div>
    );
    let moreComing = (
      <div key={99} className="row">
        <div className="col-10 col-sm-10 col-md-10 col-lg-10 col-xl-10 offset-1 offset-sm-1 offset-md-1 offset-lg-1 offset-xl-1">
          <span>
            <i className="moreComingArrow icono-arrow1-right"></i>
          </span>
          <div className="font6 moreComing">More coming soon.</div>
        </div>
      </div>
    );



    if (this.state.projects) {
      return (<div>
        title,
        {this.state.projects.map((project, i) => {
          
            
              <ScrollAnimation delay={150} key={i} animateOnce={true} animateIn="slideInRight">
                <Showcase
                       id={i}
                       imgLink={`${project.headerImg}`}
                       title={`${project.showcaseTitle}`}
                />
              </ScrollAnimation>
              
          
        })},
      moreComing],</div>);
    }
  }
}

export default Showcases;
