import React from 'react';
import {Component} from 'react';
import './TheBlog.scss';
import axios from 'axios';
import Article from './Article/Article';

export default class TheBlog extends Component{
    constructor(props) {
        super(props);

        this.state={
            articles:[]
        }
    }
    componentDidMount() {
        axios
          .get("http://localhost:5000/articles/")
          .then((response) => {
            console.log(response)
            this.setState({
              articles: Object.values(response.data),
            });
          })
      }

      getMonth=(month)=>{
          let m=parseInt(month)
          switch(m){
              case 1:
                  return 'January';
              case 2:
                  return 'February';
              case 3:
                  return 'March';
              case 4:
                  return 'April';
              case 6:
                  return 'June';
                default:
                    return null;
          }
      }

    render(){

          let title =( <div>
                <div className="font5 theBlogTitle">
                    Damn Good Resources
                </div>
                <div className="font2 theBlogSubtitle">
                    To afraid to change your mind?
                </div>
            </div>)


  if (this.state.articles) {
    return [
      title,<div key={199} className="articlesList">
      {this.state.articles.map((article, i) => {

          let month=article.createdAt.substring(5,7)
          let test=this.getMonth(month)
          console.log(test)
          return (
              <Article
                     key={i}
                     id={article._id}
                     articleImg={article.articleCover}
                     articleTitle={`${article.articleTitle}aaaaaaaaaaaaaaaaa`}
                     readTime={`${article.articleDuration} min`}
                     articleDate={month}
              />
          );
      })}</div>];
  }
}




